					h.ylbl = 'Final State D*dk';
					h.sldlbl = {'t'};
					h.tlbl = 'Effective Hamiltonian paths';
				case 'heff-current'
					h.zdata = obj.getData('heff-current');				% t x D' x dk' x D x dk
					d = size(h.zdata);
					h.zdata = reshape(h.zdata, d(1),d(2)*d(3),[]);		% t x D' * dk' x D * dk
